function writeLC(key, value){
    localStorage.setItem(key, JSON.stringify(value));
}


function readLC(key){
    return JSON.parse(localStorage.getItem(key));
}


function showAlert(message) {
    const alert_element = document.createElement('div');
    alert_element.innerHTML = 
        `<h1 class="notification-auto-hide" style="color: red;position: fixed;z-index: 999;top: 70%;right: 20px;line-height: 100px; padding: 0px 40px !important;background-color: lightgreen;padding: 5px 5px;">
            ${ message }
        </h1>`;
    document.body.append(alert_element);
    setTimeout( () => document.querySelector('.notification-auto-hide').remove(), 2000);
}


function isDataEmptyOrImproper(data_array) {
    // Returns true if data_array is empty or improper
    return !data_array || data_array.length === 0 || isNaN(data_array[0].id) || isNaN(data_array[0].quantity)
}


function addCart(_id){
    // Increase the quantity of the product (with provided _id) in cart by 1
    let cart_infos = readLC(LC_CART_INFOS);

    if ( isDataEmptyOrImproper(cart_infos) ) {
        cart_infos = [];
    }

    let curr_info = cart_infos.find( info => info.id === _id );
    if (curr_info){
        const curr_index = cart_infos.indexOf(curr_info);
        const corresponding_product = PRODUCTS.find( product => product.id === _id );
        if (curr_info.quantity + 1 > corresponding_product.stock) {  // Do not allow buy more than stock limit
            showAlert(`Sorry, we have only ${ corresponding_product.stock } items in our stock.`);
            return
        }

        curr_info.quantity++;

        cart_infos[curr_index] = curr_info;
    } else {
        new_info = {};
        new_info.id = _id;
        new_info.quantity = 1;

        cart_infos.push(new_info);
    }

    showAlert('Added to your shopping cart.');
    writeLC(LC_CART_INFOS, cart_infos);
    updateCartIcon();
}


function buyNow(){
    addCart( readLC(LC_CURRENT_PRODUCT_ID) );
    window.location.href = "cart.html";  // redirect
}


function updateCartIcon(){
    //Create/update the number floating in front of cart symbol and the LC
    const cart_infos = readLC(LC_CART_INFOS);

    // Remove old number (if any) before appending the new one
    Array.from(document.querySelectorAll('.cart_number')).map( elem => elem.remove() );

    if ( isDataEmptyOrImproper(cart_infos) ){
        return
    }

    let total_item = 0;
    cart_infos.map( info => total_item += info.quantity );

    const cart_quantity_element = document.createElement('div');
    cart_quantity_element.classList.add('cart_number');
    cart_quantity_element.innerText = total_item;
    cart_quantity_element.style = 
        `position: absolute;
        background-color: lightgreen;
        border-radius: 40px;
        top: 10px;
        right: -7px;
        padding: 2px 7px;`;
    
    document.querySelector('.header_navbar_icon_cart').append(cart_quantity_element);
}


